import React, { useState } from "react";
import "./AboutPage.css";
import NavBar from "../../components/NavBar/NavBar";
import Banner from "../../components/Banner/Banner";
import TopBar from "../../components/TopBar/TopBar";

function AboutPage() {

  const [imageData, setImageData] = useState(null);
  const [name, setName] = useState("");
    const [body, setBody] = useState("");

    // handleAddressChange(): 
    // Helper function for setting 
    // the address of the contact page.
    const handleNameChange = (str) => {
        setName(str);
        console.log(`This is str ${str}`)
    }

    // handlePhoneChange(): 
    // Helper function for setting 
    // the phone number of the contact page.
    const handleBodyChange = (str) => {
        setBody(str);
        console.log(`This is str ${str}`)
    }

  const convertImageToBase64 = (file, callback) => {
    const reader = new FileReader();

    reader.onloadend = function() {
      const str64 = reader.result.split(",")[1];
      callback(str64);
    }

    reader.readAsDataURL(file);
  }

  const handleImageChange = (img) => {
    convertImageToBase64(img, function(str64) {
      setImageData({
        name: img.name,
        data: str64
      });
    });
  }

  return (
    <>
      <div className="about-container">
        <TopBar />
        <Banner />
        <div className="body">
          <NavBar />
          <div className="about-content">
            <form onSubmit={handleSubmit}>
              <ComponentImage
                label="Image"
                value={imageData}
                fn={handleImageChange}
              />
              <ComponentEntry
                label="Name"
                value={name}
                fn={handleNameChange}
              /> 
              <ComponentEntry
                label="Description"
                value={body}
                fn={handleBodyChange}
              /> 
              <ComponentSubmit />
            </form>           
          </div>
        </div>
      </div>
    </>
  );
}

export default AboutPage;
